    function _fgUBC(item_type, to_level, item_inst){
        //is called when showing build buildings
        var next_details = inventory._fgID({"type":item_type, "level": to_level});
        var totals = {"diamonds":0};
        for(var c in state.currencies){
            if(next_details["upgrade_"+c] > state.storage_contents[c]){
                var requires = next_details["upgrade_"+c] - state.storage_contents[c];
                if(requires > 0){
                    if(!totals[c]){totals[c] = 0;}
                    totals[c] = totals[c] + requires;
                    totals["diamonds"] = totals["diamonds"] + iapgems._fgG4R(requires, c);
                }
            }
        }
        totals["diamonds"] = totals["diamonds"] + iapgems._fgG4R(next_details["upgrade_time"], "time");
        return totals;
    };

