function doubleDigit(n){  //gui.js, 
    if (typeof(n) == "string"){
        n = n.trim();
        if(n.length == 0){
            n = "00";
        }
        if(n.length == 1){
            n = "0" + n;
        }
    }else{
        if (n < 10){ n = "0"+n;}
    }
    return n;
}
