function updateAttackCount(){
    var c = getAttackCount();
    setActionsAvailableCount("attack_count", c);
    /* DISABLED BY morten
    if(c){
        smgr.playMusic(sound_events["music_battle_suspense"]);
    }
    */
}
function getAttackCount(){
    var c = 0;
    var radars = inventory._fgIBT("radar");
    for(radar in radars){
        c = c + radar.count;
    }
    if((c === 0) && (game_state._fgBa().length === 0)){ 
        c = 1; //else we assume he is ready to run a scan
        var hq_level = game_state._fgHQL();
        var xp = game_state._fgX();
        if ((hq_level === 1) && ((xp.level+xp.count) === 1) && (inventory._fgIC("defense") === 0)) { c = 0; }; // check if hes is still intutorial mode
    }else{
        var attack_battle_targets = JSON.parse(sessionStorage.getItem("attack_battle_targets") || "{\"venues\":[]}");
        for(var i in attack_battle_targets["venues"]){
            //FIXME: lets filter out some of the best battles
        }
        c = attack_battle_targets["venues"].length;
    }
    return c;    
}
function guiAttackButton(){
    var attack_count = getAttackCount();
    if(attack_count){ //we have specifict targets, lets show a dialog box about them to the player
        guiAlert(StringFormat(_lang["gui_attack_button_targets_message"], {"attack_count": attack_count}), {"title":_lang["gui_attack_button_targets_title"]});
    }
    showAttackLocations();
}

function showAttackLocations(){
    var game_mode = game_state._fgGM();
    if((game_mode == "economy") || (game_mode == "tutorial")){
        guiSelectResetMapClear();
        showAttackLocationsValidateFunds();
        var position = game_state._fgAL();
        var defence_weapons = game_state._fgD();
        if (!defence_weapons.length){ guiAlert(_lang["gui_warning_message_a2312"], {"level": -1, "title": "Ops ...", "sound_notification": "ui_action_not_possible", "error_level":"gui_warning_message_a2312"}); return; }
        fetchAttackLocations(position, function(){
                mmgr._fvA("resource");
        }, {state: -666, radius: game_state._fgSR()});    
        fireGameEvent("ui_attack_click");
        game_state._fsGM("attack_countdown");
        setTimeout(function(){
            smgr.playMusic(sound_events["music_battle_suspense"]);
        }, 1000);
    }
}


